import React, { Component } from "react";
import styled from "styled-components";

const BallerLifeSection = ({monthlyCallback, yearsCallback}) => {
  return (
    <div>
      <MonthlySavingsTitle1>Monthly Savings</MonthlySavingsTitle1>
      <HorizontalContainer gradientImage="Gradient_p25W5xb.png">
        <Button onClick={() => monthlyCallback(200)}>
          <p>200</p>
        </Button>
        <Button onClick={() => monthlyCallback(300)}>
          <p>300</p>
        </Button>
        <Button onClick={() => monthlyCallback(400)}>
          <p>400</p>
        </Button>
        <Button onClick={() => monthlyCallback(500)}>
          <p>500</p>
        </Button>
      </HorizontalContainer>
      <NumberOfYears1>Number of Years</NumberOfYears1>
      <HorizontalContainer gradientImage="Gradient_p25W5xb.png">
        <Button onClick={() => yearsCallback(5)}>
          <p>5</p>
        </Button>
        <Button onClick={() => yearsCallback(10)}>
          <p>10</p>
        </Button>
        <Button onClick={() => yearsCallback(15)}>
          <p>15</p>
        </Button>
        <Button onClick={() => yearsCallback(20)}>
          <p>20</p>
        </Button>
      </HorizontalContainer>
    </div>
  );
}

const HorizontalContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: stretch;
  width: 544px;
  height: 60px;
`;

const MonthlySavingsContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: stretch;
  width: 544px;
  height: 60px;
`;

const MonthlySavingsTitle1 = styled.span`
  font-family: Roboto;
  font-style: normal;
  font-weight: 400;
  color: rgba(255,255,255,1);
  text-align: center;
  font-size: 18px;
  margin-top: 10px;
`;

const Button = styled.Button`
  width: 60px;
  height: 37px;
  background-color: #E6E6E6;
  margin: 10px;
  flex-direction: column;
  display: flex;
  border: none;
`;

const Text = styled.span`
  font-family: Roboto;
  font-style: normal;
  font-weight: 400;
  color: #121212;
  text-align: center;
  line-height: 35px;
  flex: 1 1 0%;
  margin-bottom: 3px;
  display: flex;
  flex-direction: column;
`;

const NumberOfYears1 = styled.span`
  font-family: Roboto;
  font-style: normal;
  font-weight: 400;
  color: rgba(255,255,255,1);
  text-align: center;
  font-size: 18px;
  margin-top: 16px;
`;

const NumbersOfYears = styled.div`
  height: 60px;
  flex-direction: row;
  justify-content: space-between;
  align-items: stretch;
  margin-top: 1px;
  margin-left: 15px;
  margin-right: 30px;
  display: flex;
`;



export default BallerLifeSection;
