import React, { Component } from "react";
import styled from "styled-components";

const BallerLifeSection = ({monthlyCallback, yearsCallback}) => {
  return (
    <div>
      <MonthlySavingsTitle1>Monthly Savings</MonthlySavingsTitle1>
      <HorizontalContainer gradientImage="Gradient_p25W5xb.png">
        <ButtonContainer onClick={() => monthlyCallback(200)}>
          <ContentButton>
            <Text>200</Text>
          </ContentButton>
        </ButtonContainer>
        <ButtonContainer onClick={() => monthlyCallback(300)}>
          <ContentButton>
            <Text>300</Text>
          </ContentButton>
        </ButtonContainer>
        <ButtonContainer onClick={() => monthlyCallback(400)}>
          <ContentButton>
            <Text>400</Text>
          </ContentButton>
        </ButtonContainer>
        <ButtonContainer onClick={() => monthlyCallback(500)}>
          <ContentButton>
            <Text>500</Text>
          </ContentButton>
        </ButtonContainer>
      </HorizontalContainer>
      <NumberOfYears1>Number of Years</NumberOfYears1>
      <HorizontalContainer gradientImage="Gradient_p25W5xb.png">
        <ButtonContainer onClick={() => yearsCallback(5)}>
          <ContentButton>
            <Text>5</Text>
          </ContentButton>
        </ButtonContainer>
        <ButtonContainer onClick={() => yearsCallback(10)}>
          <ContentButton>
            <Text>10</Text>
          </ContentButton>
        </ButtonContainer>
        <ButtonContainer onClick={() => yearsCallback(15)}>
          <ContentButton>
            <Text>15</Text>
          </ContentButton>
        </ButtonContainer>
        <ButtonContainer onClick={() => yearsCallback(20)}>
          <ContentButton>
            <Text>20</Text>
          </ContentButton>
        </ButtonContainer>
      </HorizontalContainer>
    </div>
  );
}

const HorizontalContainer = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: stretch;
  width: 544px;
  height: 60px;
`;

const MonthlySavingsTitle1 = styled.span`
  font-family: Roboto;
  font-style: normal;
  font-weight: 400;
  color: rgba(255,255,255,1);
  text-align: center;
  font-size: 18px;

  display: block;
`;

const ButtonContainer = styled.div`
  width: 60px;
  height: 37px;
  background-color: #E6E6E6;
  margin: 10px;
  flex-direction: column;
  display: flex;
  border: none;
`;

const ContentButton = styled.button`
  display: block;
  background: none;
  height: 100%;
  width: 100%;
  border:none
 `;

const Text = styled.span`
  font-family: Roboto;
  font-style: normal;
  font-weight: 400;
  color: #121212;
  text-align: center;
  line-height: 35px;
  flex: 1 1 0%;
  display: flex;
  flex-direction: column;
`;

const NumberOfYears1 = styled.span`
  font-family: Roboto;
  font-style: normal;
  font-weight: 400;
  color: rgba(255,255,255,1);
  text-align: center;
  font-size: 18px;
  margin-top: 16px;
  display: block;
`;

export default BallerLifeSection;
