import React, { useState, useEffect } from "react";
import styled, { css } from "styled-components";
import EarlyLifeSection from "./EarlyLifeSection";

const Calculator = () => {
  const [initialEarlyLifeAmount, setEarlyLifeInitialDeposit] = useState(5000);
  const [earlyLifeMonthlySavings, setEarlyLifeMonthlySavings] = useState(200);
  const [earlyLifeYears, setEarlyLifeYears] = useState(5);
  const [ballerYears, setBallerYears] = useState(null);
  const [ballerSavings, setBallerSavings] = useState(null);
  const [totalSavings, setTotalSavings] = useState("");

  useEffect(() => {
    calculateSavings();
  }, [initialEarlyLifeAmount, earlyLifeMonthlySavings, earlyLifeYears, ballerYears, ballerSavings]);

  const calculateSavings = () => {
    const interestRate = parseFloat(10) / 100;

    const principal = parseFloat(initialEarlyLifeAmount);
    const monthlyContributions = parseFloat(earlyLifeMonthlySavings);
    const numYears = parseInt(earlyLifeYears);

    const futureValue =
      principal * Math.pow(1 + interestRate, numYears) +
      (monthlyContributions * (Math.pow(1 + interestRate, numYears) - 1)) / interestRate;

    if (ballerSavings != null && ballerYears != null) {
      const ballerLifeInitialDeposit = futureValue.toFixed(2);

      const principalBaller = parseFloat(ballerLifeInitialDeposit);
      const monthlyContributionsBaller = parseFloat(ballerSavings);
      const numYearsBaller = parseInt(ballerYears);

      const total =
        principalBaller * Math.pow(1 + interestRate, numYearsBaller) +
        (monthlyContributionsBaller * (Math.pow(1 + interestRate, numYearsBaller) - 1)) / interestRate;

      setTotalSavings(total.toFixed(2));
    } else {
      setTotalSavings(futureValue.toFixed(2));
    }
  };

  const selectBallerLifeSavings = (value) => {
    setBallerSavings(value);
  };

  const selectBallerLifeYears = (value) => {
    setBallerYears(value);
  };

  const earlyStageDepositChanged = (value) => {
    setEarlyLifeInitialDeposit(value);
  };

  const earlyLifeMonthlySavingsChanged = (value) => {
    setEarlyLifeMonthlySavings(value);
  };

  const earlyLifeYearsChanged = (value) => {
    setEarlyLifeYears(value);
  };

  return (
    <>
      <Header>
        <MillionairMakerStack>
          <MillionairMaker>Millionair Maker</MillionairMaker>
          <InitialDeposit
            defaultValue={initialEarlyLifeAmount}
            selectTextOnFocus={true}
            maxLength={10}
            onInput={(e) => earlyStageDepositChanged(e.target.value)}
          ></InitialDeposit>
          <Label>Starting Cash</Label>
        </MillionairMakerStack>
      </Header>
      <EarlyTitle>Early Life savings phase</EarlyTitle>
      <EarlyLifeSection monthlyCallback={earlyLifeMonthlySavingsChanged} yearsCallback={earlyLifeYearsChanged}/>
      <BallerLifeTitle>Baller Life savings phase</BallerLifeTitle>
      <BallerLife>
      <MonthlySavingsTitle1>Monthly Savings</MonthlySavingsTitle1>
      <MonthlySavingsContainer gradientImage="Gradient_p25W5xb.png">
        <Button>
          <ButtonOverlay>
            <LoremIpsum>200</LoremIpsum>
          </ButtonOverlay>
        </Button>
        <Group>
          <ButtonOverlay>
            <Text>300</Text>
          </ButtonOverlay>
        </Group>
        <Group2>
          <ButtonOverlay>
            <Text2>400</Text2>
          </ButtonOverlay>
        </Group2>
        <Group3>
          <ButtonOverlay>
            <Text3>500</Text3>
          </ButtonOverlay>
        </Group3>
      </MonthlySavingsContainer>
      <NumberOfYears1>Number of Years</NumberOfYears1>
      <NumbersOfYears gradientImage="Gradient_p25W5xb.png">
        <Button2>
          <ButtonOverlay>
            <Text4>5</Text4>
          </ButtonOverlay>
        </Button2>
        <Button3>
          <ButtonOverlay>
            <Text5>10</Text5>
          </ButtonOverlay>
        </Button3>
        <Button4>
          <ButtonOverlay>
            <Text6>15</Text6>
          </ButtonOverlay>
        </Button4>
        <Button5>
          <ButtonOverlay>
            <Text7>20</Text7>
          </ButtonOverlay>
        </Button5>
      </NumbersOfYears>
    </BallerLife>
      <Worth>
        <HowRichAmI>How rich am I?</HowRichAmI>
        <TotalSavingsLabelRow>
          <TotalSavingsLabel>Total savings:</TotalSavingsLabel>
          <TotalSavingsResult>{totalSavings}</TotalSavingsResult>
        </TotalSavingsLabelRow>
      </Worth>
    </>
  );

}

