import React, { useState } from 'react';
import { Slider } from "material-ui-slider";
import _ from 'lodash';

const SliderWithSnaps = (callback, min, max, interval) => {
  const [value, setValue] = useState(200);

  const handleSliderChange = (newValue) => {
    setValue(newValue);
  };

  const handleSliderSnap = (newValue) => {
    const snappedValue = _.clamp(Math.round(newValue / 100) * 100, 200, 600);
    setValue(snappedValue);

    callback(snappedValue);
  };

  return (
    <div>
      <Slider
            min={200}
            max={600}
            step={100}
            value={value}
            onChange={handleSliderChange}
            onAfterChange={handleSliderSnap}
            renderThumb={(props, state) => (
              <div {...props}>{state.valueNow}</div>
            )}
            renderTrack={(props, state) => (
              <div
                {...props}
                style={{
                  ...props.style,
                  backgroundColor: state.index === 1 ? '#007BFF' : '#CCC',
                }}
              />
            )}
          />
          <p>Selected Value: {value}</p>
    </div>
  );
};

export default SliderWithSnaps;
