import React, { useState } from 'react';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css';

const SliderWithSnaps = ({ callback, min, max, interval }) => {
  const [value, setValue] = useState(min);

  const handleSliderChange = (newValue) => {
    setValue(newValue);
  };

  const handleSliderSnap = (newValue) => {
    const snappedValue = Math.round(newValue / interval) * interval;
    setValue(snappedValue);

    callback(snappedValue);
  };

  const marks = {};
  for (let i = min; i <= max; i += interval) {
    marks[i] = i;
  }

  const selectedMarkStyle = {
    fontWeight: 'bold',
    height: '16px',
    color: '#007BFF',
  };

  return (
    <div style={{ margin: '20px' }}>
      <Slider
        min={min}
        max={max}
        step={interval}
        value={value}
        onChange={handleSliderChange}
        onAfterChange={handleSliderSnap}
        marks={marks}
        railStyle={{ backgroundColor: '#ccc' }}
        trackStyle={{ backgroundColor: '#007BFF' }}
        handleStyle={{ borderColor: '#007BFF' }}
        dotStyle={{ display: 'none' }}
      />
    </div>
  );
};

export default SliderWithSnaps;
