import React, { useState } from "react";
import styled, { css } from "styled-components";

const Calculator = () => {
    const [initialAmount, setInitialAmount] = useState('');
    const [monthlySavings, setMonthlySavings] = useState('');
    const [annualInterestRate, setAnnualInterestRate] = useState('');
    const [years, setYears] = useState('');
    const [ballerYears, setBallerYears] = useState('');
    const [ballerSavings, setBallerSavings] = useState('');
    const [totalSavings, setTotalSavings] = useState(0);

    const calculateSavings = () => {
      const principal = parseFloat(initialAmount);
      const monthlyContributions = parseFloat(monthlySavings);
      const interestRate = parseFloat(annualInterestRate) / 100;
      const numYears = parseInt(years);

      const futureValue = principal * Math.pow(1 + interestRate, numYears) +
        (monthlyContributions * (Math.pow(1 + interestRate, numYears) - 1)) / interestRate;

      setTotalSavings(futureValue.toFixed(2));
    };

