import React, { useState } from "react";
import styled, { css } from "styled-components";
import EarlyLifeSection from "./EarlyLifeSection";

const Calculator = () => {
  const [initialEarlyLifeAmount, setEarlyLifeInitialDeposit] = useState('');
  const [earlyLifeMonthlySavings, setEarlyLifeMonthlySavings] = useState('');
  const [earlyLifeYears, setEarlyLifeYears] = useState('');
  const [ballerYears, setBallerYears] = useState('');
  const [ballerSavings, setBallerSavings] = useState('');
  const [totalSavings, setTotalSavings] = useState('');

  const calculateSavings = () => {
    const interestRate = parseFloat(10) / 100;

    var principal = parseFloat(initialEarlyLifeAmount);
    var monthlyContributions = parseFloat(earlyLifeMonthlySavings);

    var numYears = parseInt(earlyLifeYears);

    const futureValue = principal * Math.pow(1 + interestRate, numYears) +
      (monthlyContributions * (Math.pow(1 + interestRate, numYears) - 1)) / interestRate;

    const ballerLifeInitialDeposit = futureValue.toFixed(2);

    principal = parseFloat(ballerLifeInitialDeposit);
    monthlyContributions = parseFloat(ballerSavings);
    numYears = parseInt(ballerYears);

    const total = principal * Math.pow(1 + interestRate, numYears) +
    (monthlyContributions * (Math.pow(1 + interestRate, numYears) - 1)) / interestRate;

    setTotalSavings(total.toFixed(2));

    console.log(total.toFixed(2));
  };

  const selectBallerLifeSavings = (value) => {
    console.log("Baller savings " + value);
    setBallerSavings(value);

    calculateSavings();
  };

  const selectBallerLifeYears = (value) => {
    console.log("Baller years " + value);
    setBallerYears(value);

    calculateSavings();
  };

  const earlyStageDepositChanged = (value) => {
    setEarlyLifeInitialDeposit(value);

    calculateSavings();
  };

  const earlyLifeMonthlySavingsChanged = (value) => {
    console.log(value);
    setEarlyLifeMonthlySavings(value);

    calculateSavings();
  };

  const earlyLifeYearsChanged = (value) => {
    console.log(value);
    setEarlyLifeYears(value);

    calculateSavings();
  };

  return (
    <>
      <Header>
        <MillionairMakerStackStack>
          <MillionairMakerStack>
            <MillionairMaker>Millionair Maker</MillionairMaker>
            <InitialDeposit
              placeholder=""
              defaultValue="200"
              selectTextOnFocus={true}
              maxLength={10}
              onInput={(e) => earlyStageDepositChanged(e.target.value)}
            ></InitialDeposit>
          </MillionairMakerStack>
          <Label>Starting Cash</Label>
        </MillionairMakerStackStack>
      </Header>
      <EarlyTitle>Early Life savings phase</EarlyTitle>
      <EarlyLifeSection monthlyCallback={earlyLifeMonthlySavingsChanged} yearsCallback={earlyLifeYearsChanged}/>
      <BallerLifeTitle>Baller Life savings phase</BallerLifeTitle>
      <BallerLifeGroupStack>
        <BallerLifeGroup>
          <BallerLife>
            <MonthlySavingsTitle1>Monthly Savings</MonthlySavingsTitle1>
            <MonthlySavingsContainer gradientImage="Gradient_p25W5xb.png">
              <button onClick={() => selectBallerLifeSavings(200)}>
                  <p>200</p>
              </button>
              <button onClick={() => selectBallerLifeSavings(300)}>
                  <p>300</p>
              </button>
              <button onClick={() => selectBallerLifeSavings(400)}>
                  <p>400</p>
              </button>
              <button onClick={() => selectBallerLifeSavings(500)}>
                  <p>500</p>
              </button>
            </MonthlySavingsContainer>
            <NumberOfYears1>Number of Years</NumberOfYears1>
            <MonthlySavingsContainer gradientImage="Gradient_p25W5xb.png">
            <button onClick={() => selectBallerLifeYears(5)}>
                  <p>5</p>
              </button>
              <button onClick={() => selectBallerLifeYears(10)}>
                  <p>10</p>
              </button>
              <button onClick={() => selectBallerLifeYears(15)}>
                  <p>15</p>
              </button>
              <button onClick={() => selectBallerLifeYears(20)}>
                  <p>20</p>
              </button>
            </MonthlySavingsContainer>
          </BallerLife>
        </BallerLifeGroup>
      </BallerLifeGroupStack>
      <Worth>
        <HowRichAmI>How rich am I?</HowRichAmI>
        <TotalSavingsLabelRow>
          <TotalSavingsLabel>Total savings:</TotalSavingsLabel>
          <TotalSavingsResult>{totalSavings}</TotalSavingsResult>
        </TotalSavingsLabelRow>
      </Worth>
    </>
  );

}

