import React, { Component } from "react";
import styled, { css } from "styled-components";
import { Slider } from "material-ui-slider";
import SliderWithSnaps from "./SliderWithSnaps";

const EarlyLifeSection = ({monthlyCallback, yearsCallback}) => {
  return (
    <EarlyLife>
      <MonthlySavingsTitle>Monthly Savings</MonthlySavingsTitle>
      <SliderWithSnaps 
          callback={monthlyCallback} 
          min={200} 
          max={600} 
          interval={100}
        />
      <NumberOfYears>Number of Years</NumberOfYears>
      <SliderWithSnaps 
        callback={yearsCallback} 
        min={5} 
        max={20} 
        interval={5}
      />
    </EarlyLife>
  );
}

const EarlyLife = styled.div`
  background-color: #ff7201;
  flex-direction: column;
  display: flex;
  flex: 1 1 0%;
`;

const MonthlySavingsTitle = styled.span`
  font-family: Roboto;
  top: 0px;
  left: 54px;
  position: absolute;
  font-style: normal;
  font-weight: 400;
  color: rgba(255,255,255,1);
  height: 28px;
  width: 206px;
  text-align: center;
  font-size: 18px;
`;

const MonthlySavingsTitleStack = styled.div`
  width: 318px;
  height: 57px;
  margin-top: 24px;
  margin-left: 29px;
  position: relative;
`;

const NumberOfYears = styled.span`
  font-family: Roboto;
  font-style: normal;
  font-weight: 400;
  color: rgba(255,255,255,1);
  height: 28px;
  width: 206px;
  text-align: center;
  font-size: 18px;
  margin-top: 48px;
  margin-left: 83px;
`;

export default EarlyLifeSection;
