import React, { useState } from 'react';
import Slider from 'rc-slider';
import styled, { css } from "styled-components";
import 'rc-slider/assets/index.css';

const SliderWithSnaps = ({ callback, min, max, interval }) => {
  const [value, setValue] = useState(min);

  const handleSliderChange = (newValue) => {
    setValue(newValue);
  };

  const handleSliderSnap = (newValue) => {
    const snappedValue = Math.round(newValue / interval) * interval;
    setValue(snappedValue);

    callback(snappedValue);
  };

  const marks = {};
  for (let i = min; i <= max; i += interval) {
    marks[i] = i;
  }

  const selectedMarkStyle = {
    fontWeight: 'bold',
    height: '16px',
    color: '#007BFF',
  };

  const regularMarkStyle = {
    fontWeight: 'regular',
    height: '16px',
    color: '#ffffff',
  };

  const handleStyle = {
    borderColor: '#007BFF',
    borderWidth: '7px',
    height: '30px',
    width: '30px',
    marginTop: '-10px',
    backgroundColor: '#fff',
  };

  const dotStyle = {
    backgroundColor: '#fff',
  };

  return (
    <Container>
      <Slider
        min={min}
        max={max}
        step={interval}
        value={value}
        onChange={handleSliderChange}
        onAfterChange={handleSliderSnap}
        railStyle={{ backgroundColor: '#ccc', height: '8px', borderRadius: '4px' }}
        trackStyle={{ backgroundColor: '#007BFF', height: '8px', borderRadius: '4px' }}
        handleStyle={handleStyle}
        dotStyle={dotStyle}
      />
      <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '10px' }}>
  {Array.from({ length: (max - min) / interval + 1 }).map((_, index) => {
    const mark = min + index * interval;
    const isMarkSelected = value === mark;

    return (
      <div key={mark}>
        <div
          style={{
            width: '3px',
            height: '20px',
            backgroundColor: isMarkSelected ? '#007BFF' : '#ffffff',
            marginTop: '-10px',
            marginLeft:'-10px',
          }}
        />
        <div
          style={{
            textAlign: 'center',
            fontSize: '12px',
            marginTop: '10px',
            marginLeft: '-8px',
            ...(isMarkSelected ? selectedMarkStyle : regularMarkStyle),
          }}
        >
          {mark}
        </div>
      </div>
    );
  })}
</div>
    </Container>
  );
};

const Container = styled.div`
  margin: 30px;
  height: 100%;
`;

export default SliderWithSnaps;
